using System.Collections;using System.Collections.Generic;using UnityEngine;public class CollisionDetection : MonoBehaviour{    //public float forcePush;    private void OnCollisionEnter2D(Collision2D coll2d)    {        if (coll2d.gameObject.tag == "Walls") // push back after reaching wall        {            Rigidbody2D rb2d = GetComponent<Rigidbody2D>();            float x = rb2d.position.x;            float y = rb2d.position.y;

            Vector2 movement = new Vector2(x, y).normalized;

            rb2d.velocity = - movement * 3;        }    }    //private void OnControllerColliderHit(ControllerColliderHit hit)    //{    //    if (hit.gameObject.tag == "Walls") // push back after reaching wall    //    {    //        Rigidbody2D rb2d = hit.gameObject.GetComponent<Rigidbody2D>();    //        float x = rb2d.position.x;    //        float y = rb2d.position.y;    //        rb2d.angularVelocity = -x * 100;    //        rb2d.velocity = -transform.up * y * 100;    //    }    //}}